using System;
using System.Collections.Generic;
using System.Linq;
using System.Text;
using System.Threading.Tasks;
namespace Decoding
{
    internal class Program
    {

        public static int[] DecodingToASCII(string input)
        {
            int[] result = new int[input.Length / 8];

            for (int i = 0; i < input.Length; i += 8)
            {
                result[i / 8] = Convert.ToInt32(input.Substring(i, 8), 2) ;

            }
            return result;
        }

        public static string ASCIIToEnglish(int[] input)
        {
            string result = "";

            foreach (int che in input)
            {
                result += (char)che;
            }

            return result;
        }
        static void Main(string[] args)
        {
            string input = "";
            Console.WriteLine(ASCIIToEnglish(DecodingToASCII(input)));

        }
    }
}
